import React, { useState, useEffect } from 'react';
import { BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, LineChart, Line } from 'recharts';
import { AlertCircle, TrendingUp, Package, Zap, Filter, Plus, Search, X, CheckCircle2, Clock } from 'lucide-react';

const API_URL = 'http://localhost:8000';

const COLORS = {
  Equipment: '#ef4444',
  Quality: '#f59e0b',
  'Supply Chain': '#3b82f6',
  Technical: '#8b5cf6',
  Other: '#6b7280'
};

const SEVERITY_COLORS = {
  critical: '#dc2626',
  high: '#ea580c',
  medium: '#ca8a04',
  low: '#65a30d'
};

export default function IssueFlow() {
  const [issues, setIssues] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [inputText, setInputText] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [filterSeverity, setFilterSeverity] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [showInput, setShowInput] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchIssues();
    fetchAnalytics();
  }, [filterCategory, filterSeverity]);

  const fetchIssues = async () => {
    try {
      setLoading(true);
      setError(null);
      let url = `${API_URL}/issues`;
      const params = new URLSearchParams();
      if (filterCategory !== 'all') params.append('category', filterCategory);
      if (filterSeverity !== 'all') params.append('severity', filterSeverity);
      if (params.toString()) url += `?${params.toString()}`;

      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch issues');
      const data = await response.json();
      setIssues(data.issues || []);
    } catch (error) {
      console.error('Failed to fetch issues:', error);
      setError('Failed to load issues. Is the backend running?');
    } finally {
      setLoading(false);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await fetch(`${API_URL}/analytics`);
      if (!response.ok) throw new Error('Failed to fetch analytics');
      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Failed to fetch analytics:', error);
    }
  };

  const handleSubmit = async () => {
    if (!inputText.trim()) return;

    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_URL}/issues`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: inputText })
      });

      if (!response.ok) throw new Error('Failed to submit issue');

      setInputText('');
      setShowInput(false);
      setSuccessMessage('Issue submitted successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
      await fetchIssues();
      await fetchAnalytics();
    } catch (error) {
      console.error('Failed to submit issue:', error);
      setError('Failed to submit issue. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const filteredIssues = issues.filter(issue =>
    searchTerm === '' || 
    issue.raw_text.toLowerCase().includes(searchTerm.toLowerCase()) ||
    issue.entities.some(e => e.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const categoryData = analytics?.by_category ? 
    Object.entries(analytics.by_category).map(([name, value]) => ({ name, value })) : [];

  const severityData = analytics?.by_severity ?
    Object.entries(analytics.by_severity).map(([name, value]) => ({ name, value })) : [];

  const trendData = analytics?.trend || [];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50">
      <style>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateX(-20px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes pulse {
          0%, 100% {
            opacity: 1;
          }
          50% {
            opacity: 0.5;
          }
        }

        @keyframes shimmer {
          0% {
            background-position: -1000px 0;
          }
          100% {
            background-position: 1000px 0;
          }
        }

        .animate-fade-in-up {
          animation: fadeInUp 0.5s ease-out;
        }

        .animate-slide-in {
          animation: slideIn 0.3s ease-out;
        }

        .glass-effect {
          background: rgba(255, 255, 255, 0.7);
          backdrop-filter: blur(10px);
          -webkit-backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.3);
        }

        .glass-effect-dark {
          background: rgba(255, 255, 255, 0.9);
          backdrop-filter: blur(20px);
          -webkit-backdrop-filter: blur(20px);
          border: 1px solid rgba(255, 255, 255, 0.4);
        }

        .gradient-border {
          position: relative;
          background: white;
          border-radius: 1rem;
        }

        .gradient-border::before {
          content: '';
          position: absolute;
          inset: -2px;
          border-radius: 1rem;
          padding: 2px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
          -webkit-mask-composite: xor;
          mask-composite: exclude;
        }

        .hover-lift {
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }

        .hover-lift:hover {
          transform: translateY(-4px);
          box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
        }

        .shimmer {
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.5), transparent);
          background-size: 1000px 100%;
          animation: shimmer 2s infinite;
        }

        .category-badge {
          position: relative;
          overflow: hidden;
        }

        .category-badge::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
          transition: left 0.5s;
        }

        .category-badge:hover::before {
          left: 100%;
        }

        .stat-card {
          position: relative;
          overflow: hidden;
        }

        .stat-card::after {
          content: '';
          position: absolute;
          top: -50%;
          right: -50%;
          width: 200%;
          height: 200%;
          background: radial-gradient(circle, rgba(255,255,255,0.1) 0%, transparent 70%);
          opacity: 0;
          transition: opacity 0.3s;
        }

        .stat-card:hover::after {
          opacity: 1;
        }

        .pulse-ring {
          animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }

        input:focus, textarea:focus, select:focus {
          outline: none;
          box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1);
        }

        .custom-scrollbar::-webkit-scrollbar {
          width: 8px;
        }

        .custom-scrollbar::-webkit-scrollbar-track {
          background: rgba(0, 0, 0, 0.05);
          border-radius: 4px;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: rgba(99, 102, 241, 0.3);
          border-radius: 4px;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
          background: rgba(99, 102, 241, 0.5);
        }
      `}</style>

      {/* Animated Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-72 h-72 bg-purple-300 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute bottom-20 right-10 w-72 h-72 bg-pink-300 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse" style={{animationDelay: '1s'}}></div>
        <div className="absolute top-1/2 left-1/2 w-72 h-72 bg-indigo-300 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse" style={{animationDelay: '2s'}}></div>
      </div>

      {/* Header */}
      <header className="glass-effect shadow-lg border-b border-white/30 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex justify-between items-center">
            <div className="animate-slide-in">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl flex items-center justify-center shadow-lg">
                  <AlertCircle className="text-white" size={28} />
                </div>
                <div>
                  <h1 className="text-3xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
                    IssueFlow
                  </h1>
                  <p className="text-sm text-slate-600 font-medium">Self-organizing operational intelligence</p>
                </div>
              </div>
            </div>
            <button
              onClick={() => setShowInput(!showInput)}
              className="flex items-center gap-2 bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-6 py-3 rounded-xl hover:from-indigo-700 hover:to-purple-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105"
            >
              <Plus size={20} />
              <span className="font-semibold">Report Issue</span>
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 relative">
        {/* Success Message */}
        {successMessage && (
          <div className="fixed top-24 right-8 z-50 animate-slide-in">
            <div className="glass-effect-dark px-6 py-4 rounded-xl shadow-2xl flex items-center gap-3">
              <CheckCircle2 className="text-green-600" size={24} />
              <span className="text-slate-800 font-medium">{successMessage}</span>
            </div>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="glass-effect-dark border-l-4 border-red-500 px-6 py-4 rounded-xl mb-6 animate-fade-in-up shadow-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <AlertCircle className="text-red-600" size={24} />
                <span className="text-slate-800 font-medium">{error}</span>
              </div>
              <button onClick={() => setError(null)} className="text-slate-600 hover:text-slate-800">
                <X size={20} />
              </button>
            </div>
          </div>
        )}

        {/* Input Form Modal */}
        {showInput && (
          <div className="fixed inset-0 bg-black/30 backdrop-blur-sm z-40 flex items-center justify-center p-4" onClick={() => setShowInput(false)}>
            <div className="glass-effect-dark rounded-2xl shadow-2xl max-w-2xl w-full animate-fade-in-up" onClick={(e) => e.stopPropagation()}>
              <div className="p-8">
                <div className="flex justify-between items-center mb-6">
                  <h2 className="text-2xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
                    Report New Issue
                  </h2>
                  <button onClick={() => setShowInput(false)} className="text-slate-500 hover:text-slate-700 transition-colors">
                    <X size={24} />
                  </button>
                </div>
                <div>
                  <textarea
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="Describe the issue in natural language...

Examples:
 Motor overheating after 3 hours
 PCB board version 2 failed QA
 Delay in shipment from vendor X"
                    className="w-full h-40 p-4 border-2 border-slate-200 rounded-xl focus:border-indigo-500 transition-all duration-300 resize-none text-slate-700 placeholder-slate-400"
                  />
                  <div className="flex gap-3 mt-6">
                    <button
                      onClick={handleSubmit}
                      disabled={loading || !inputText.trim()}
                      className="flex-1 bg-gradient-to-r from-indigo-600 to-purple-600 text-white px-6 py-4 rounded-xl hover:from-indigo-700 hover:to-purple-700 disabled:from-slate-300 disabled:to-slate-300 disabled:cursor-not-allowed transition-all duration-300 font-semibold shadow-lg hover:shadow-xl transform hover:scale-105 disabled:transform-none"
                    >
                      {loading ? (
                        <span className="flex items-center justify-center gap-2">
                          <div className="w-5 h-5 border-3 border-white border-t-transparent rounded-full animate-spin"></div>
                          Processing...
                        </span>
                      ) : 'Submit Issue'}
                    </button>
                    <button
                      onClick={() => setShowInput(false)}
                      className="px-6 py-4 border-2 border-slate-300 rounded-xl hover:bg-slate-50 transition-all duration-300 font-semibold"
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Analytics Dashboard */}
        {analytics && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="stat-card glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up">
              <div className="flex items-center gap-4">
                <div className="p-4 bg-gradient-to-br from-blue-400 to-blue-600 rounded-2xl shadow-lg">
                  <AlertCircle className="text-white" size={28} />
                </div>
                <div>
                  <p className="text-sm text-slate-600 font-medium mb-1">Total Issues</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                    {analytics.total_issues}
                  </p>
                </div>
              </div>
            </div>

            <div className="stat-card glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up" style={{animationDelay: '0.1s'}}>
              <div className="flex items-center gap-4">
                <div className="p-4 bg-gradient-to-br from-red-400 to-red-600 rounded-2xl shadow-lg pulse-ring">
                  <Zap className="text-white" size={28} />
                </div>
                <div>
                  <p className="text-sm text-slate-600 font-medium mb-1">Critical Issues</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-red-600 to-red-800 bg-clip-text text-transparent">
                    {analytics.by_severity.critical || 0}
                  </p>
                </div>
              </div>
            </div>

            <div className="stat-card glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up" style={{animationDelay: '0.2s'}}>
              <div className="flex items-center gap-4">
                <div className="p-4 bg-gradient-to-br from-purple-400 to-purple-600 rounded-2xl shadow-lg">
                  <Package className="text-white" size={28} />
                </div>
                <div>
                  <p className="text-sm text-slate-600 font-medium mb-1">Categories</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-purple-600 to-purple-800 bg-clip-text text-transparent">
                    {Object.keys(analytics.by_category).length}
                  </p>
                </div>
              </div>
            </div>

            <div className="stat-card glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up" style={{animationDelay: '0.3s'}}>
              <div className="flex items-center gap-4">
                <div className="p-4 bg-gradient-to-br from-green-400 to-green-600 rounded-2xl shadow-lg">
                  <TrendingUp className="text-white" size={28} />
                </div>
                <div>
                  <p className="text-sm text-slate-600 font-medium mb-1">Last 7 Days</p>
                  <p className="text-3xl font-bold bg-gradient-to-r from-green-600 to-green-800 bg-clip-text text-transparent">
                    {trendData.reduce((sum, d) => sum + d.count, 0)}
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Charts */}
        {analytics && categoryData.length > 0 && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            <div className="glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up">
              <h3 className="text-xl font-bold text-slate-800 mb-6 flex items-center gap-2">
                <div className="w-1 h-6 bg-gradient-to-b from-indigo-500 to-purple-500 rounded-full"></div>
                Issues by Category
              </h3>
              <ResponsiveContainer width="100%" height={280}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={(entry) => `${entry.name}: ${entry.value}`}
                    outerRadius={100}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {categoryData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[entry.name] || COLORS.Other} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift animate-fade-in-up" style={{animationDelay: '0.1s'}}>
              <h3 className="text-xl font-bold text-slate-800 mb-6 flex items-center gap-2">
                <div className="w-1 h-6 bg-gradient-to-b from-indigo-500 to-purple-500 rounded-full"></div>
                Severity Distribution
              </h3>
              <ResponsiveContainer width="100%" height={280}>
                <BarChart data={severityData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.1)" />
                  <XAxis dataKey="name" stroke="#64748b" />
                  <YAxis stroke="#64748b" />
                  <Tooltip />
                  <Bar dataKey="value" fill="url(#colorGradient)" radius={[8, 8, 0, 0]} />
                  <defs>
                    <linearGradient id="colorGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="0%" stopColor="#6366f1" stopOpacity={0.8}/>
                      <stop offset="100%" stopColor="#8b5cf6" stopOpacity={0.8}/>
                    </linearGradient>
                  </defs>
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="glass-effect-dark rounded-2xl shadow-lg p-6 hover-lift lg:col-span-2 animate-fade-in-up" style={{animationDelay: '0.2s'}}>
              <h3 className="text-xl font-bold text-slate-800 mb-6 flex items-center gap-2">
                <div className="w-1 h-6 bg-gradient-to-b from-indigo-500 to-purple-500 rounded-full"></div>
                7-Day Trend
              </h3>
              <ResponsiveContainer width="100%" height={280}>
                <LineChart data={trendData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.1)" />
                  <XAxis dataKey="date" stroke="#64748b" />
                  <YAxis stroke="#64748b" />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="count" stroke="#6366f1" strokeWidth={3} dot={{ r: 6 }} activeDot={{ r: 8 }} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        )}

        {/* Filters */}
        <div className="glass-effect-dark rounded-2xl shadow-lg p-6 mb-6 animate-fade-in-up">
          <div className="flex flex-wrap gap-4 items-center">
            <div className="flex items-center gap-2">
              <Filter size={20} className="text-indigo-600" />
              <span className="font-bold text-slate-800">Filters</span>
            </div>

            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="px-4 py-2 border-2 border-slate-200 rounded-xl focus:border-indigo-500 transition-all duration-300 font-medium text-slate-700 bg-white"
            >
              <option value="all">All Categories</option>
              <option value="Equipment">Equipment</option>
              <option value="Quality">Quality</option>
              <option value="Supply Chain">Supply Chain</option>
              <option value="Technical">Technical</option>
            </select>

            <select
              value={filterSeverity}
              onChange={(e) => setFilterSeverity(e.target.value)}
              className="px-4 py-2 border-2 border-slate-200 rounded-xl focus:border-indigo-500 transition-all duration-300 font-medium text-slate-700 bg-white"
            >
              <option value="all">All Severities</option>
              <option value="critical">Critical</option>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>

            <div className="flex-1 min-w-[200px]">
              <div className="relative">
                <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400" size={20} />
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search issues..."
                  className="w-full pl-12 pr-4 py-3 border-2 border-slate-200 rounded-xl focus:border-indigo-500 transition-all duration-300 font-medium text-slate-700 bg-white"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Issues List */}
        <div className="glass-effect-dark rounded-2xl shadow-lg overflow-hidden animate-fade-in-up">
          <div className="p-6 border-b border-slate-200 bg-gradient-to-r from-indigo-50 to-purple-50">
            <h2 className="text-2xl font-bold text-slate-800 flex items-center gap-2">
              <Clock size={24} className="text-indigo-600" />
              Recent Issues
            </h2>
            <p className="text-sm text-slate-600 mt-1 font-medium">
              {filteredIssues.length} {filteredIssues.length === 1 ? 'issue' : 'issues'} found
            </p>
          </div>

          <div className="divide-y divide-slate-200 custom-scrollbar max-h-[600px] overflow-y-auto">
            {loading ? (
              <div className="p-12 text-center">
                <div className="inline-block w-12 h-12 border-4 border-indigo-200 border-t-indigo-600 rounded-full animate-spin"></div>
                <p className="text-slate-600 mt-4 font-medium">Loading issues...</p>
              </div>
            ) : filteredIssues.length === 0 ? (
              <div className="p-12 text-center">
                <AlertCircle className="mx-auto text-slate-300 mb-4" size={48} />
                <p className="text-slate-500 font-medium">No issues found</p>
                <p className="text-slate-400 text-sm mt-2">Click "Report Issue" to create your first one!</p>
              </div>
            ) : (
              filteredIssues.map((issue, index) => (
                <div key={issue.id} className="p-6 hover:bg-gradient-to-r hover:from-indigo-50/50 hover:to-purple-50/50 transition-all duration-300 animate-slide-in" style={{animationDelay: `${index * 0.05}s`}}>
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-3">
                        <span
                          className="category-badge px-4 py-1.5 rounded-full text-xs font-bold shadow-sm"
                          style={{
                            backgroundColor: `${COLORS[issue.category]}20`,
                            color: COLORS[issue.category],
                            border: `2px solid ${COLORS[issue.category]}40`
                          }}
                        >
                          {issue.category}
                        </span>
                        <span
                          className="px-4 py-1.5 rounded-full text-xs font-bold text-white shadow-sm"
                          style={{ backgroundColor: SEVERITY_COLORS[issue.severity] }}
                        >
                          {issue.severity.toUpperCase()}
                        </span>
                        <span className="text-xs text-slate-500 font-medium flex items-center gap-1">
                          <Clock size={14} />
                          {new Date(issue.timestamp).toLocaleString()}
                        </span>
                      </div>

                      <p className="text-slate-800 mb-3 font-medium leading-relaxed">{issue.raw_text}</p>

                      {issue.entities.length > 0 && (
                        <div className="flex flex-wrap gap-2">
                          {issue.entities.map((entity, idx) => (
                            <span
                              key={idx}
                              className="px-3 py-1 bg-gradient-to-r from-slate-100 to-slate-50 text-slate-700 rounded-lg text-xs font-semibold border border-slate-200 shadow-sm hover:shadow-md transition-shadow"
                            >
                              {entity}
                            </span>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
