import React, { useState, useEffect } from 'react';
import { BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, LineChart, Line } from 'recharts';
import { AlertCircle, TrendingUp, Package, Zap, Filter, Plus, Search } from 'lucide-react';

// CHANGE THIS to your deployed backend URL when deploying
const API_URL = 'http://localhost:8000';

const COLORS = {
  Equipment: '#ef4444',
  Quality: '#f59e0b',
  'Supply Chain': '#3b82f6',
  Technical: '#8b5cf6',
  Other: '#6b7280'
};

const SEVERITY_COLORS = {
  critical: '#dc2626',
  high: '#ea580c',
  medium: '#ca8a04',
  low: '#65a30d'
};

export default function IssueFlow() {
  const [issues, setIssues] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(false);
  const [inputText, setInputText] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [filterSeverity, setFilterSeverity] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [showInput, setShowInput] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchIssues();
    fetchAnalytics();
  }, [filterCategory, filterSeverity]);

  const fetchIssues = async () => {
    try {
      setLoading(true);
      setError(null);
      let url = `${API_URL}/issues`;
      const params = new URLSearchParams();
      if (filterCategory !== 'all') params.append('category', filterCategory);
      if (filterSeverity !== 'all') params.append('severity', filterSeverity);
      if (params.toString()) url += `?${params.toString()}`;

      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch issues');
      const data = await response.json();
      setIssues(data.issues || []);
    } catch (error) {
      console.error('Failed to fetch issues:', error);
      setError('Failed to load issues. Is the backend running?');
    } finally {
      setLoading(false);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await fetch(`${API_URL}/analytics`);
      if (!response.ok) throw new Error('Failed to fetch analytics');
      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      console.error('Failed to fetch analytics:', error);
    }
  };

  const handleSubmit = async () => {
    if (!inputText.trim()) return;

    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_URL}/issues`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: inputText })
      });

      if (!response.ok) throw new Error('Failed to submit issue');

      setInputText('');
      setShowInput(false);
      await fetchIssues();
      await fetchAnalytics();
    } catch (error) {
      console.error('Failed to submit issue:', error);
      setError('Failed to submit issue. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const filteredIssues = issues.filter(issue =>
    searchTerm === '' || 
    issue.raw_text.toLowerCase().includes(searchTerm.toLowerCase()) ||
    issue.entities.some(e => e.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const categoryData = analytics?.by_category ? 
    Object.entries(analytics.by_category).map(([name, value]) => ({ name, value })) : [];

  const severityData = analytics?.by_severity ?
    Object.entries(analytics.by_severity).map(([name, value]) => ({ name, value })) : [];

  const trendData = analytics?.trend || [];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-slate-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-slate-900">IssueFlow</h1>
              <p className="text-sm text-slate-600 mt-1">Self-organizing operational intelligence</p>
            </div>
            <button
              onClick={() => setShowInput(!showInput)}
              className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              <Plus size={20} />
              Report Issue
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-800 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {/* Input Form */}
        {showInput && (
          <div className="bg-white rounded-xl shadow-lg p-6 mb-8 border border-slate-200">
            <h2 className="text-xl font-semibold text-slate-900 mb-4">Report New Issue</h2>
            <div>
              <textarea
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                placeholder="Describe the issue in natural language...&#10;Examples:&#10;• Motor overheating after 3 hours&#10;• PCB board version 2 failed QA&#10;• Delay in shipment from vendor X"
                className="w-full h-32 p-4 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              />
              <div className="flex gap-3 mt-4">
                <button
                  onClick={handleSubmit}
                  disabled={loading || !inputText.trim()}
                  className="flex-1 bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 disabled:bg-slate-300 disabled:cursor-not-allowed transition-colors font-medium"
                >
                  {loading ? 'Processing...' : 'Submit Issue'}
                </button>
                <button
                  onClick={() => setShowInput(false)}
                  className="px-6 py-3 border border-slate-300 rounded-lg hover:bg-slate-50 transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Analytics Dashboard */}
        {analytics && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <div className="flex items-center gap-3">
                <div className="p-3 bg-blue-100 rounded-lg">
                  <AlertCircle className="text-blue-600" size={24} />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Total Issues</p>
                  <p className="text-2xl font-bold text-slate-900">{analytics.total_issues}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <div className="flex items-center gap-3">
                <div className="p-3 bg-red-100 rounded-lg">
                  <Zap className="text-red-600" size={24} />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Critical</p>
                  <p className="text-2xl font-bold text-slate-900">{analytics.by_severity.critical || 0}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <div className="flex items-center gap-3">
                <div className="p-3 bg-purple-100 rounded-lg">
                  <Package className="text-purple-600" size={24} />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Categories</p>
                  <p className="text-2xl font-bold text-slate-900">{Object.keys(analytics.by_category).length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <div className="flex items-center gap-3">
                <div className="p-3 bg-green-100 rounded-lg">
                  <TrendingUp className="text-green-600" size={24} />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Last 7 Days</p>
                  <p className="text-2xl font-bold text-slate-900">{trendData.reduce((sum, d) => sum + d.count, 0)}</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Charts */}
        {analytics && categoryData.length > 0 && (
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <h3 className="text-lg font-semibold text-slate-900 mb-4">Issues by Category</h3>
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={(entry) => `${entry.name}: ${entry.value}`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {categoryData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[entry.name] || COLORS.Other} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200">
              <h3 className="text-lg font-semibold text-slate-900 mb-4">Severity Distribution</h3>
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={severityData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#3b82f6" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="bg-white rounded-xl shadow-sm p-6 border border-slate-200 lg:col-span-2">
              <h3 className="text-lg font-semibold text-slate-900 mb-4">7-Day Trend</h3>
              <ResponsiveContainer width="100%" height={250}>
                <LineChart data={trendData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="count" stroke="#3b82f6" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        )}

        {/* Filters */}
        <div className="bg-white rounded-xl shadow-sm p-6 mb-6 border border-slate-200">
          <div className="flex flex-wrap gap-4 items-center">
            <div className="flex items-center gap-2">
              <Filter size={20} className="text-slate-600" />
              <span className="font-medium text-slate-700">Filters:</span>
            </div>

            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="px-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Categories</option>
              <option value="Equipment">Equipment</option>
              <option value="Quality">Quality</option>
              <option value="Supply Chain">Supply Chain</option>
              <option value="Technical">Technical</option>
            </select>

            <select
              value={filterSeverity}
              onChange={(e) => setFilterSeverity(e.target.value)}
              className="px-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Severities</option>
              <option value="critical">Critical</option>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>

            <div className="flex-1 min-w-[200px]">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400" size={20} />
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search issues..."
                  className="w-full pl-10 pr-4 py-2 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Issues List */}
        <div className="bg-white rounded-xl shadow-sm border border-slate-200">
          <div className="p-6 border-b border-slate-200">
            <h2 className="text-xl font-semibold text-slate-900">Recent Issues</h2>
            <p className="text-sm text-slate-600 mt-1">
              {filteredIssues.length} {filteredIssues.length === 1 ? 'issue' : 'issues'} found
            </p>
          </div>

          <div className="divide-y divide-slate-200">
            {loading ? (
              <div className="p-12 text-center text-slate-500">Loading...</div>
            ) : filteredIssues.length === 0 ? (
              <div className="p-12 text-center text-slate-500">
                No issues found. Click "Report Issue" to create your first one!
              </div>
            ) : (
              filteredIssues.map((issue) => (
                <div key={issue.id} className="p-6 hover:bg-slate-50 transition-colors">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <span
                          className="px-3 py-1 rounded-full text-xs font-medium"
                          style={{
                            backgroundColor: `${COLORS[issue.category]}20`,
                            color: COLORS[issue.category]
                          }}
                        >
                          {issue.category}
                        </span>
                        <span
                          className="px-3 py-1 rounded-full text-xs font-medium text-white"
                          style={{ backgroundColor: SEVERITY_COLORS[issue.severity] }}
                        >
                          {issue.severity.toUpperCase()}
                        </span>
                        <span className="text-xs text-slate-500">
                          {new Date(issue.timestamp).toLocaleString()}
                        </span>
                      </div>

                      <p className="text-slate-900 mb-3">{issue.raw_text}</p>

                      {issue.entities.length > 0 && (
                        <div className="flex flex-wrap gap-2">
                          {issue.entities.map((entity, idx) => (
                            <span
                              key={idx}
                              className="px-2 py-1 bg-slate-100 text-slate-700 rounded text-xs"
                            >
                              {entity}
                            </span>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
